	{
		if(eventData.usrmeta !== undefined)
		{
			device = eventData.startCh.channel.instance;

           if(activeData[device] !== undefined)
			{

				activeData[device].data.push(parseFloat(eventData.value));
 			}
			else
			{
				console.log("Doesn't exist in database");
			}

		}
 	}
	else
	{
		if(eventData.usrmeta !== undefined)
		{
			UpdateDevices();
		}

	}

	return result;
});

function UpdateDevices()
{
    for(var currentDevice in devices)
	{
		var selected = devices[currentDevice];


	    if(activeData[selected.Name].data.length > 0)
        {        
            var min = Math.min.apply(Math, activeData[selected.Name].data);    // 1
            var max = Math.max.apply(Math, activeData[selected.Name].data);


            selected.MinValue = min;
            selected.MaxValue = max;
            selected.AvgValue = average(activeData[selected.Name].data); 
            devices[selected.sid] = selected;
            var trapId = selected.Id.toString();
            Database.saveRecordParam("refrigerations", "Device", {
            	trapId: selected
            }, function (data) {

            });
        }
		activeData[selected.Name].data = [];
	}
}

function average(nums) {
    var sum = 0;
    for( var i = 0; i < nums.length; i++ ){
        sum += parseFloat(nums[i]);
    }
    return sum/nums.length;
}

