 function CheckRowStatus(temperature) {
	var result = 0;
	var importantRisen = 0;
	var green = 0;

	if (!IsDecimal(temperature)) {
		result = 1;
 		return result;
	}


	if(parseFloat(temperature) > AmberLower && parseFloat(temperature) < AmberUpper)
    {
        green = 1;
    }

	if (parseFloat(temperature) < RedUpper && parseFloat(temperature) >= AmberUpper) {
		importantRisen = 1;
	}
	if (parseFloat(temperature) > RedLower && parseFloat(temperature) <= AmberLower) {
		importantRisen = 1;
	}

	if (importantRisen === 1) {
 		result = 4;
	}
	else if (green == 1) {
 		result = 2;
	}
	else {  
 		result = 3;
	}


	return result;
}

function evenRound(num, decimalPlaces) {
	var d = decimalPlaces || 0;
	var m = Math.pow(10, d);
	var n = +(d ? num * m : num).toFixed(8); // Avoid rounding errors
	var i = Math.floor(n), f = n - i;
	var e = 1e-8; // Allow for rounding errors in f
	var r = (f > 0.5 - e && f < 0.5 + e) ?
		((i % 2 == 0) ? i : i + 1) : Math.round(n);
	return d ? r / m : r;
}

var IsDecimal = function(num){
    return ((num.toString().split('.').length) <= 2 && num.toString().match(/^[\+\-]?\d*\.?\d+(?:[Ee][\+\-]?\d+)?$/)) ? (!isNaN(parseFloat(num))) : false ;
};

function ValidateAlert(data, alertType)
{
	var result ={};
