	var alert;
	switch(alertType)
	{
		case "RedAlertExpired":
			alert = 3;
			break;
		case "AmberAlertExpired":
			alert = 4;
			break;
	}

	for(var item in data)
	{
	//	console.log(data[item]);
		if(data[item] === 2)
		{
		   sendSignal = false;
		}
	}
	result.AlertType = alert;
    result.alertReult = sendSignal;
	return result;
}



