/**
 * Response to incoming flow message. eventData object passed in with:
 * eventData.value - incoming data value (string)
 * eventData.startCh - state of channel that initiated the flow and the channel details (Object. Members {channel}, {value})
 * @return {string} return a value to continue the flow or to cancel flow return null
 */
Script.on("flow", function (eventData) {
	// Get the message source device name
	var device = eventData.startCh.channel.instance.charAt(0).toUpperCase() + eventData.startCh.channel.instance.slice(1).toUpperCase();
	var subject = "Bait box '" + device + "' has not communicated for " + eventData.value;
		console.log(device);

 	var selectTraps = [
		{
			"TableName": "Device", // Specify the primary table used to construct the join request
			"Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
			"Columns":
				[
					"Id",
					"Owner",
					"CustomerId"


				], // Specify the list of columns to take from the reference table.
			"Filters": // Filter options in case they are needed.
				[
					{
						"Column": "Name", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
						"ParamName": "DeviceNumber", // Parameter name, must be unique and should not contain $ as its resolved by the mapper. 
						"Value": device.toString() // Value that you want to pass for comparison (any object)
					}
				]
		}];


	var readRecord = Database.readCompound("refrigerations", "Devices", selectTraps);
	// Get trapper associated with the trap
	//var dbResult = Database.readRecords("traps","State","Technician", "DeviceNumber='" + device + "'");
	var recordData = JSON.parse(readRecord);
	console.log(recordData.data);
	console.log(recordData);


	// Do we have a trapper?
	if (Object.keys(recordData.data).length > 0) {
		for (var item in recordData.data) {
			var dto = recordData.data[item];
			var trapId = dto[0];
			Database.saveRecordParam("refrigerations", "Device",  {
				trapId:{
					"Id": trapId,
					"CommunicationError": 1
				}
			} , function (data) {

			});
			var trapper = dto[1];
 			var email = "undefined";
			var customerEmail = GetCustomerEmail(dto[1]);
			var dirInfo = JSON.parse(Directory.getUsers({ "users": [trapper], columns: ["email"] }));
			console.log(dirInfo.data[trapper]);

			var subject = "Alert Device Status Offline";
		    var emails = ["dean.dobson@pestsense.com", "kristiformilchev615@gmail.com", "petar@collaborativesoft.com"];
 			if(dirInfo.data[trapper] !== undefined)
			{
				emails.push(dirInfo.data[trapper][0]);
			}
			if(customerEmail !== null)
			{
				emails.push(customerEmail);
			}
			console.log(customerEmail);
			console.log(emails);
			return JSON.stringify({
				"recipients": emails, "subject": subject, "body": "Message from Sensahub Automated Alerts: Please check the status of " +
					"device '" + device + "' as it has stopped communicating, either no cellular signal or battery is flat."
			});
		}
	}
	else {
		System.writeLog("INFO - Device heartbeat timed out but no user associated with device '" + device + "', so can't send warning email. Check device signal strength and/or battery.");
	}
});

